import React from 'react';


class Cart extends React.Component{
    constructor(props){
        super(props);
        this.state={
            total:0
        }
    }

    showTotal = (total) => {
        let newTotal = this.state.total + total
        this.setState({
            total:newTotal
        })
    }

    removeItem = (key,item) => {
        this.props.removeFromCart(key);
        if(item.onSale){
            this.showTotal(-(item.price*.4))
        }else{
            this.showTotal(item.price)
        }
    }

    componentDidMount(){
        if(this.props.cartItems.length > 0){
            let total = 0;
            for(let i =0; i< this.props.cartItems.length; i++){
                if(this.props.cartItems[i].onSale){
                    const price = this.props.cartItems[i].price * .4;
                }else{
                    const price = this.props.cartItems[i].price
                }

                total += price;
            }
            this.showTotal(total);
        }else{
            this.showTotal(0)
        }

    }

    render(){
        return(
            <div className='cart'>
                <h3>Cart</h3>
                {this.props.cartItems.map((item,key)=>(
                    <div className='cartItem' key={key}>
                        <input type="button" value="Remove Item" onClick={()=>this.removeItem(key,item)}/>
                        <img src={item.image} alt={item.item} />
                        <h4>{item.item}</h4>
                        {item.onSale ? <h4>${(item.price*.4).toFixed(2)}</h4> : <h4>${(item.price).toFixed(2)}</h4>}

                    </div>

                ))}
                <div className="total">
                    <h4>Total: {this.state.total}</h4>
                </div>
            </div>
        )
    }

}


export default Cart;
