import React from 'react';


class Cart extends React.Component{
    constructor(props){
        super(props);
        this.state={
            total:0
        }
    }

    showTotal = (newTotal) => {

        this.setState({
            total:newTotal
        })
    }

    componentDidMount(){
        if(this.props.cartItems.length > 0){
            let total = 0;
            for(let i =0; i< this.props.cartItems.length; i++){
                const price = this.props.cartItems[i].price;
                total += price;
            }
            this.showTotal(total);
        }

    }

    componentDidUpdate(prevProps,prevState){

            let total = 0;
            for(let i =0; i< this.props.cartItems.length; i++){
                const price = this.props.cartItems[i].price;
                total += price;

            if(prevState.total !== total){
                this.showTotal(total);
            }

        }

    }

    render(){
        return(
            <div className='cart'>
                <h3>Cart</h3>
                {this.props.cartItems.map((item,key)=>(
                    <div className='cartItem' key={key}>
                        <input type="button" value="Remove Item" onClick={()=>this.props.removeFromCart(key)}/>
                        <img src={item.image} alt={item.item} />
                        <h4>{item.item}</h4>
                        {item.onSale ? <h4>${(item.price*.4).toFixed(2)}</h4> : <h4>${(item.price).toFixed(2)}</h4>}

                    </div>

                ))}
                <div className="total">
                    <h4>Total: {this.state.total}</h4>
                </div>
            </div>
        )
    }

}


export default Cart;
