import React from 'react';


class Cart extends React.Component{


    render(){
        return(
            <div className='cart'>
                <h3>Cart</h3>
                {this.props.cartItems.map((item,key)=>(
                    <div className='cartItem' key={key}>
                        <input type="button" value="Remove Item" onClick={()=>this.props.removeFromCart(key)}/>
                        <img src={item.image} alt={item.item} />
                        <h4>{item.item}</h4>
                        {item.onSale ? <h4>${item.price*.4}</h4> : <h4>${item.price}</h4>}

                    </div>
                ))}
            </div>
        )
    }

}


export default Cart;
