    constructor(props){
        super(props);
        this.state = {
            items:data,
            view:'home',
            cartItems:[],
            formInputs:{
                item:"",
                image:"",
                price:0,
                rating:0,
                description:"",
                onSale:false,
                subscription:false,
                category:""
            },
            showItem:{}
        }
    }

    handleView = (view,showId) =>{
        console.log(showId);
        const newShowItem = this.state.items[showId]
