        //         rating:updatedData.rating,
        //         description:updatedData.description,
        //         onSale:updatedData.onSale,
        //         subscription:updatedData.subscription,
        //         category:updatedData.category
        //     }
        // }
        this.setState({
            view:view,
            showItem: newShowItem
            // formInputs:formInputs
        })
    }

