import React from 'react';

class Show extends React.Component{


    render(){
        return(
            <div className='show'>
                <div className='itemImgContainer'>
                    <img src={this.props.item.image} alt={this.props.item.item} />
                </div>
                <div className='itemDetailsContainer'>
                    <h3>{this.props.item.item}</h3>
                    {/*Determine if item is on sale. If so, show original price and the new price*/}
                    {this.props.item.onSale? <h4 className='onSale'>${this.props.item.price} <span>${this.props.item.price*.4}</span></h4> : <h4>${this.props.item.price}</h4>}


                    <h5>Rating: {this.props.item.rating}</h5>
                    <article>
                        <h4>Description:</h4>
                        <p>{this.props.item.description}</p>
                    </article>
                </div>
            </div>
        )
    }

}


export default Show;
